import React, { Component } from "react";
import Card from "../card/Card";
import "./layout.css";
import Input from "../input/Input";
export default class Layout extends Component {
    constructor(props) {
        super();
        this.state = {
            data: [],
            completed: false,
            name: "",
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleOnClick = this.handleOnClick.bind(this);
    }
    async fecthArtistData() {
        fetch(
            "https://itunes.apple.com/search?term=" +
                this.state.name +
                "&media=music&entity=album&attribute=artistTerm&limit=500"
        )
            .then((response) => {
                if (response.status !== 200) {
                    console.log(
                        "there was a problem. Status Code: " + response.status
                    );
                    return;
                }
                if (response.resultCount == 0) {
                    console.log("result 0");
                }
                response.json().then((data) => {
                    console.log(data);
                    this.setState({
                        data: data.results,
                    });
                });
            })
            .catch(function (error) {
                console.log("Error:", error);
                this.setState({ error, isLoading: false });
            });
    }

    handleChange = (e) => {
        this.setState({
            name: e.target.value,
        });
    };

    handleOnClick() {
        this.fecthArtistData();
        this.setState({
            completed: true,
        });
    }
    render() {
        return (
            <div className="container">
                <Input
                    handleChange={this.handleChange}
                    handleOnClick={this.handleOnClick}
                />
                {this.state.completed === true
                    ? this.data.map((ele) => {
                          return (
                              <Card
                                  artwork={ele.artworkUrl100}
                                  artistName={ele.artistName}
                                  albumName={ele.collectionCensoredName}
                                  artistViewUrl={ele.artistViewUrl}
                              />
                          );
                      })
                    : null}
            </div>
        );
    }
}
